import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { FlightRm } from  './search-flight/search-flight.component'// Adjust the import path if necessary

@Injectable({
  providedIn: 'root'
})
export class FlightService {

  private apiUrl = 'https://localhost:7169/Flight'; // Your API endpoint

  constructor(private http: HttpClient) { }

  getFlights(): Observable<FlightRm[]> {
    return this.http.get<FlightRm[]>(this.apiUrl);
  }
}
